mapper.select:
SELECT * FROM poem 
ORDER BY RAND() 
LIMIT #{limit}

SELECT PID, Title, Poet, Text, Category, Background, Appreciation, Translation
FROM poem
WHERE PID = #{poemId}

SELECT PID, Title, Poet, Text, Category, Background, Appreciation, Translation FROM poem WHERE PID IN
#{pid}
ORDER BY PID

SELECT COUNT(*) > 0
FROM poem
WHERE PID = #{poemId}
